import React, { useEffect, useRef, useState } from 'react';

const filterType = {
    showAll: "SHOW_ALL",
    showCompleted: "SHOW_COMPLETED",
}

function List(props) {
    // const searchInput = useRef(null);

    // props.mode === "ADD" && searchInput.current.focus();

    const [todo, setTodo] = useState({});

    const filteredData = props.filterType === filterType.showCompleted ? props.data.filter(item => !item.completed) : props.data

    function handleChange(index) {
        return (e) => {
            let todoIndex = index;
            console.log(todoIndex);
            let isChecked = e.target.checked;
            props.setData(isChecked, index);
        }
    }

    function onEdit(index) {
        return (e) => {
            setTodo(prevState => ({ ...prevState, [e.target.name]: e.target.value }));
            props.editData(e.target.value, index);
        }
    }

    return(
        <div>
            {filteredData.map((item, i) => (
                <div className = "input" key={i}>
                <input type="checkbox" onChange={handleChange(i)}/>
                    {props.mode === "EDIT" ? 
                        <input name={i} value={todo === '' ? item.todo : todo} onChange={onEdit(i)}/> :
                    (props.mode === "ADD" && i === filteredData.length-1) ?
                        <input name={i} value={item.todo} onChange={onEdit(i)}/> :
                        <input name={i} value={item.todo} readOnly/>
                    }
                </div>
            ))}
        </div>
    )
}

export default List;
