import React, { useEffect, useRef, useState } from 'react';

function List(props) {
    const searchInput = useRef(null);

    // useEffect(()=>{
    //     searchInput.current.focus();
    //  },[])

    const filteredData = props.filterType === 'showCompleted' ? props.data : props.data.filter(item => !item.completed)

    function handleChange(index) {
        return (event) => {
            let todoIndex = index;
            console.log(todoIndex);
            let isChecked = e.target.checked;
            props.setData(isChecked, index);
        }
    }

    function onEdit(e, index) {
        props.editData(e.target.value, index);
    }

    return(
        <div>
            {filteredData.map((item, i) => (
                <div className = "input" id={i}>
                <input type="checkbox" onChange={onChange(i)}/>
                    {item.focus ?
                        <input ref={searchInput} value={item.todo} onChange={onEdit}/> :
                        <input value={item.todo} onChange={onEdit}/>
                    }
                </div>
            ))}
        </div>
    )
}

export default List;
