import React, { useState } from 'react';
import {filterType} from './Constants';

function List(props) {
    const filteredData = props.filterType === filterType.hideCompleted ? props.data.filter(item => !item.completed) : props.data
    const [todos, setTodos] = useState(filteredData);

    function handleChange(index) {
        return (e) => {
            let isChecked = e.target.checked;
            setTodos(todos[index].completed = e.target.checked)
            props.setData(isChecked, index);
        }
    }

    function onEdit(index) {
        return (e) => {
            setTodos(todos[index].todo = e.target.value);
            // setTodo( prevState => ({...prevState, [e.target.name] : e.target.value}))
            props.editData(e.target.value, index);
        }
    }

    return(
        <div>
            {console.log(todos[0].todo)}
            {console.log(todos[0].completed)}
            {todos.map((todo, i) => (
                <div className = "input" key={i}>
                <input type="checkbox" id={i} name={i} disabled={props.mode === "EDIT"} checked={todo.completed} onChange={handleChange(i)}/>
                    {props.mode === "EDIT" ? 
                        <input id={i} name={i} value={todo.todo} onChange={onEdit(i)}/> :
                    (props.mode === "ADD" && i === filteredData.length-1) ?
                        <input id={i} name={i} value={todo.todo} onChange={onEdit(i)}/> :
                        <input id={i} name={i} value={todo.todo} readOnly/>
                    }
                </div>
            ))}
        </div>
    )
}

export default List;
