import React, { useRef, useState } from 'react';
import List from './List';
import './styles.css';

function App() {
  const initialData = [
    {
      id: 1,
      todo: "Call mom",
      completed: true,
      focus: false,
    },
    {
      id: 2,
      todo: "Eat lunch",
      completed: false,
      focus: true,
    }
  ]

  const filterType = {
    showAll: "SHOW_ALL",
    showCompleted: "SHOW_COMPLETED",
  }

  const modeType = {
    add: "ADD",
    edit: "EDIT",
    base: "BASE",
  }

  const [data, setData] = useState(initialData);
  const [filter, setFilterType] = useState(filterType.showAll);
  const [mode, setMode] = useState(modeType.base);

  function plusClicked() {
      let newId = data.length;
      let newData = data;

      newData.push({
          id: newId,
          todo: "",
          completed: false,
          focus: true,
      })

      setData(newData);
      setMode("")
  }

  function doneClicked() {
    let newData = data;
    data[data.length-1].todo === "" && newData.pop()

    setData(newData);
    setEditing(false);
    setAdding(false);
  }

  function editClicked() {
    setEditing(true);
  }

  return (
    <>
      <div className="buttons">
          {adding || editing ? 
                (<button className="button doneButton" onClick={doneClicked}>Done</button>)
              :
              (
                <>
                <button className="button editButton" onClick={editClicked}>Edit</button>
                <button className="button plusButton" onClick={plusClicked}>+</button> 
                </>
              )
          }
      </div>
      <div className="body-container">
          <div className="title">
              <h1 className="underline">To-do list</h1>
          </div>
      </div>
      <List
        data={data} 
        filterType={filter}
        setData={(isComplete, index) => {
          let newData = data;
          newData[index].completed = isComplete;
          setData(newData);
          console.log('new data change complete' + JSON.stringify(newData))
        }}
        editData={(newTodo, index) => {
          let newData = data;
          newData[index].todo = newTodo;
          setData(newData);
          console.log('new todo edit data change complete' + JSON.stringify(newData))
        }}
      />
      <div className="footer">
              {!adding && editing && 
                <> 
                  <button className="button showCompleted" onClick={setFilterType(filterType.showCompleted)}>Show Completed</button>
                  <button className="button deleteCompleted">Delete Completed</button> 
                </>}
            </div>
    </>
  );
}

export default App;
