function List(props) {
    const searchInput = useRef(null);

    const completeTodo = (id) => {
        let updatedTodos = props.data.map(item => {
             if(todo.id === id){
                 todo.isComplete = true
              }
            return todo
       }) 
         setTodos(updatedTodos)
     };

    // useEffect(()=>{
    //     searchInput.current.focus();
    //  },[])

    const filteredData = props.filterType === 'showCompleted' ? props.data : props.data.filter(item => !item.completed)

    function handleChange(e, index) {
        console.log("index" + index);
        let isChecked = e.target.checked;
        props.setData(isChecked, index);
    }

    return(
        <div>
            {filteredData.map((item, i) => (
                <div className = "input" id={i}>
                <input type="checkbox" onChange={(i) => handleChange(i)}/>
                    {item.focus ?
                        <input ref={searchInput} value={item.todo} key={i} onChange={() => completeTodo(i)}/> :
                        <input value={item.todo} key={i} onChange={() => completeTodo(i)}/>
                    }
                </div>
            ))}
        </div>
    )
}

export default List;
